import React, { useState } from 'react';
import { connect } from 'react-redux';
import { addCustomer } from '../../actions/profile';
import PropTypes from 'prop-types';

const AddCustomer = ({ addCustomer, history }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    balance: ''
  });

  const { name, email, balance } = formData;

  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    addCustomer(formData);
    // Clear form
    setFormData({
      name: '',
      email: '',
      balance: ''
    });
  };

  return (
    <div className="container">
      <h1 className="large text-primary">Add New Customer</h1>
      <p className="lead">
        <i className="fas fa-user"></i> Create a New Customer Account
      </p>
      <form className="form" onSubmit={onSubmit}>
        <div className="form-group">
          <input
            type="text"
            placeholder="Name"
            name="name"
            value={name}
            onChange={onChange}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="email"
            placeholder="Email Address"
            name="email"
            value={email}
            onChange={onChange}
            required
          />
        </div>
        <div className="form-group">
          <input
            type="number"
            placeholder="Initial Balance"
            name="balance"
            value={balance}
            onChange={onChange}
            required
          />
        </div>
        <input type="submit" className="btn btn-primary" value="Add Customer" />
      </form>
    </div>
  );
};

AddCustomer.propTypes = {
  addCustomer: PropTypes.func.isRequired
};

export default connect(null, { addCustomer })(AddCustomer);
